import React from 'react'
import TicketCard from '../components/TicketCard'

const Dashboard = () => {

const tickets = [
  {category: 'Q1 2022',
  color : 'red',
title : 'How to be a millionaire',
owner : 'Anirudh Kadian',
avatar : 'https://miro.medium.com/max/3150/1*NpJHWyoBEy3OLAXfjQHugQ.jpeg',
status : 'done',
progress : 90,
priority : 5,
description : 'Make a video on million dollars',
timestamp : '2022=02-05'
},

{category: 'Q2 2022',
color : 'red',
title : 'How to be a billionaire',
owner : 'Anirudh',
avatar : 'https://miro.medium.com/max/3150/1*NpJHWyoBEy3OLAXfjQHugQ.jpeg',
status : 'in Progress',
progress : 70,
priority : 2,
description : 'Make a video on Billion dollars',
timestamp : '2022=02-06'
},

{category: 'Q1 2022',
color : 'blue',
title : 'How to be a Trillionaire',
owner : 'Addu Kadain ',
avatar : 'https://miro.medium.com/max/3150/1*NpJHWyoBEy3OLAXfjQHugQ.jpeg',
status : 'stuck',
progress : 30,
priority : 3,
description : 'Make a video on Trillion dollars',
timestamp : '2022=02-07'
}
]

const colors = [
  'rgb(255,179,186)',
  'rgb(255,223,186)',
  'rgb(255,225,186)',
  'rgb(186,255,201)',
  'rgb(186,255,255)',
]

// ...new Set() creates an array of objects that are unique, since the fN is run wrt categories only, an array of categories is created.
const uniqueCategories = [
  // Set creates a group of unique values
  ...new Set(tickets?.map(({category})=>category))
]

// category is the only one chosen here because we need need quarters to appear only once

  return (
    <div className="dashboard">
<h1> My Projects</h1>
    
    <div className="ticket-container">
      {/* If ticket exists, then unique category ( an array of 2 values should be mapped in a way that it created h3 with array object ) */}
{tickets && uniqueCategories?.map((uniqueCategory, _index)=> (
<div key={_index}>
<h3> {uniqueCategory}</h3>
{/* tickets is to be filtered in a way that all values except quarters ( since it is already used ) is taken into account */}
{/* since there are 3 tickets, the quarters would be filtered , an array created, would be mapped and 3 tickets without quarters would make a new array called filteredTickets */}
{/* this new array would be mapped and 3 ticket cards would be created, all the valud can be passed as props, and recieved at ticket cards */}
{tickets.filter(ticket=> ticket.category === uniqueCategory)
.map((filteredTicket, _index) => (
  <TicketCard 
  key={_index}
  id={_index}
  color={colors[_index]}
  priority={filteredTicket.priority}
  ticket={filteredTicket}
  title={filteredTicket.title}
  avatar={filteredTicket.avatar}/>
))
}
</div>

))}
    </div>
    </div>
  )
}

export default Dashboard
